mapper.select:
select m.* from Sys_Menu m
inner join Sys_RoleMenu rm on m.id = rm.menu_id
inner join Sys_Role r on r.id = rm.role_id
inner join Sys_UserRole ur on r.id = ur.role_id

    ur.user_id = #{userId}
    and m.system_code = #{systemCode}

        and m.type in

            #{type}



order by m.sort_num ASC
